package femtodb.core;/**  * FemtoDB用の固定値クラス<br> * * @author Takahiro Iwase * @licence Apache License 2.0  */public class FemtoDBConstants {    public static boolean isBuilded = false;    public static void build(String[] args) {        if (isBuilded == false) {            isBuilded = true;        }    }    /**     * トランザクションログのパス     */    public static volatile String TRANSACTION_LOG = "./op.log";    /**     * トランザクションログの書き込み要非     */    public static volatile boolean TRANSACTION_LOG_WRITE = true;    /**     * FemtoServerをhttpで起動する場合のポート番号     */    public static volatile int HTTP_SERVER_PORT = 8080;    /**     * FemtoServerをhttpで起動する場合の最大同時接続可能数     */    public static volatile int HTTP_SERVER_MAXTHREADS = 400;    /**     * FemtoServerをhttpで起動した場合のクライアントの接続後処理待ち時間     */    public static volatile int HTTP_SERVER_IDLETIMEOUT = 30000;    }