package femtodb.core.accessor.parameter;public class NormalWhereParameter {    private String columnName = null;    private int whereType = -1;    private IWhereParameter parameter = null;    /**     * where句のパラメータクラスのコンストラクタ     *     * @param columnName 対象カラム名     * @param whereType whereの条件指定     * @param parameter 条件側のパラメータ     */    public NormalWhereParameter(String columnName, int whereType, IWhereParameter parameter) {        this.columnName = columnName;        this.whereType = whereType;        this.parameter = parameter;    }    public String getColumnName() {        return this.columnName;    }    public int getWhereType() {        return this.whereType;    }    public IWhereParameter getParameter() {        return this.parameter;    }    public String toString(){        return columnName + "\t" + whereType + "\t" + parameter.toStoreString();    }}