package femtodb.core.accessor.parameter;import java.util.*;import femtodb.core.*;import femtodb.core.table.*;import femtodb.core.table.data.*;import femtodb.core.table.transaction.*;import femtodb.core.accessor.parameter.*;import femtodb.core.accessor.executor.*;import femtodb.core.accessor.scripts.*;public class WhereTypeEqual implements IWhereType {    private String whereParamStr = null;    private int whereParamStrLen = -1;    private String columnName = null;    public WhereTypeEqual(String columnName, IWhereParameter whereParameter) {        this.columnName = columnName;        whereParamStr = whereParameter.toString();        whereParamStrLen = whereParamStr.length();    }    public int type() {        return 1;    }    public final boolean execute(TableDataTransfer tableDataTransfer) {        String columnData = tableDataTransfer.getColumnData(this.columnName);        if (columnData == null) return false;        return columnData.equals(whereParamStr);    }    public final void executeAll(TableIterator iterator, TransactionNo transactionNo, List resultList) {        for (; iterator.hasNext();) {            iterator.nextEntry();            TableData tableData = (TableData)iterator.getEntryValue();            TableDataTransfer tableDataTransfer = tableData.getTableDataTransfer(transactionNo);            if (tableDataTransfer != null) {                String columnData = tableDataTransfer.getColumnData(this.columnName);                if (columnData == null) continue;                if (columnData.equals(whereParamStr)) resultList.add(tableDataTransfer);            }        }    }}