package femtodb.core.accessor.parameter;import java.util.regex.Pattern;import java.util.regex.Matcher;import femtodb.core.table.data.*;public class WhereTypeLike implements IWhereType {    private String whereParamStr = null;    private String columnName = null;    private Pattern pattern = null;    public WhereTypeLike(String columnName, IWhereParameter whereParameter) {        this.columnName = columnName;        whereParamStr = whereParameter.toString();        pattern = Pattern.compile(whereParamStr);    }    public int type() {        return 2;    }    public boolean execute(TableDataTransfer tableDataTransfer) {        String columnData = tableDataTransfer.getColumnData(this.columnName);        if (columnData == null) return false;        Matcher matcher = pattern.matcher(columnData);        if (matcher.find()) {            return true;        } else {            return false;        }    }}