package femtodb.core.accessor.parameter;import femtodb.core.table.data.*;public class WhereTypeLike implements IWhereType {    private String whereParamStr = null;    private String columnName = null;    public WhereTypeLike(String columnName, IWhereParameter whereParameter) {        this.columnName = columnName;        whereParamStr = whereParameter.toString();    }    public int type() {        return 2;    }    public boolean execute(TableDataTransfer tableDataTransfer) {        String columnData = tableDataTransfer.getColumnData(this.columnName);        if (columnData == null) return false;        if (columnData.indexOf(whereParamStr) != -1) {            return true;        } else {            return false;        }    }}