package femtodb.core.table.data;import java.io.*;abstract class AbstractLocalData implements Serializable {    protected static String getColumnNameGroup(String keyFirstChar, SerializeLocalData localData) {        int idx = createHash(keyFirstChar);        if (localData.data[idx] == null) return null;        return new String(localData.data[idx]);    }    protected static byte[] getColumnNameGroupBytes(String keyFirstChar, SerializeLocalData localData) {        int idx = createHash(keyFirstChar);        if (localData.data[idx] == null) return null;        return localData.data[idx];    }    protected static void putColumnNameGroup(String keyFirstChar, String groupStr, SerializeLocalData localData) {        int idx = createHash(keyFirstChar);        localData.data[idx] = groupStr.getBytes();    }     protected static int createHash(String keyFirstChar) {        return ((keyFirstChar.hashCode() << 1) >>> 1) % 5;    }}