package femtodb.core.table.index;import java.io.*;import java.util.*;import femtodb.core.table.data.*;/**  * NumberIndexComparatorクラス<br> * * @author Takahiro Iwase * @license Apache License 2.0  */public class NumberIndexComparator implements Comparator, Serializable {    public NumberIndexComparator() {    }    public int compare(Object t1Col, Object t2Col) {        if (t1Col == null && t2Col == null) return 0;        if (t1Col == null && t2Col != null) return -1;        if (t1Col != null && t2Col == null) return 1;        return ((String)t1Col).compareTo((String)t2Col);    }}