package femtodb.core.util;/**  * FemtoUtilクラス<br> * * @author Takahiro Iwase * @license Apache License 2.0  */public class FemtoUtil {    private FemtoUtil() {}    public static final String replaceKeyString(String key) {        if (key == null) return "null";                key = key.replaceAll("\r\n", "<femtobr>");        key = key.replaceAll("\r", "<femtobr>");        key = key.replaceAll("\n", "<femtobr>");        key = key.replaceAll("\t", "<femtotab>");        return key;    }    public static final String replaceValueString(String value) {        if (value == null) return "null";        value = value.replaceAll("\r\n", "<femtobr>");        value = value.replaceAll("\r", "<femtobr>");        value = value.replaceAll("\n", "<femtobr>");        value = value.replaceAll("\t", "<femtotab>");        return value;    }}